import React from 'react';
import PetCard from './PetCard'

class MyPetsButton extends React.Component {

  handleAddOrRemove = (currentUser, petId) => {
    console.log(currentUser)
    // console.log("petId in myPetsButton", petId)
    console.log("this.props.pet in myPetsButton", this.props.pet)
    // console.log("this.props.foundPet in myPetsButton", this.props.foundPet)
    console.log("this.props.myPets in myPetsButton", this.props.myPets)
    console.log("this.props.myAdoptedPets in myPetsButton", this.props.myAdoptedPets)
    let pet = this.props.pets.find(pet => pet.id === this.props.petId)
    console.log("pet in myPetsButton", pet)
    let myPetMatchIds = this.props.myPets.map(myPet => myPet.pet_id)
    console.log("myPetMatchIds in myPetsButton", myPetMatchIds)
    let matchedPet = this.props.myPets.find(petMatch => petMatch.pet_id === this.props.petId)
    console.log("matchedPet in myPetsButton", matchedPet)
    // if (!this.props.myAdoptedPets.includes(pet) && myPetMatchIds.includes(petId)) {
    //   return (
    //     <div>
    //       <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(this.props.currentUser.id, pet.id)}>Adopt</button>
    //       <button className="ui button" id="adopt-btn" onClick={()=>this.props.removeFromMyPets(matchedPet.id)}>Remove From My Pets</button>
    //     </div>
    //   )
    if (!pet.adopted && myPetMatchIds.includes(this.props.petId)) {
      return (
        <div>
          <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(this.props.currentUser.id, pet.id)}>Adopt</button>
          <button className="ui button" id="adopt-btn" onClick={()=>this.props.removeFromMyPets(matchedPet.id)}>Remove From My Pets</button>
        </div>
      )
  // if (!pet.adopted && !this.props.myAdoptedPets.includes(pet)) {
  //     return (
  //       <div>
  //         <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(this.props.currentUser.id, pet.id)}>Adopt</button>
  //         <button className="ui button" id="adopt-btn" onClick={()=>this.props.removeFromMyPets(matchedPet.id)}>Remove From My Pets</button>
  //       </div>
  //     )
  //   } else if (pet.adopted && !this.props.myAdoptedPets.includes(pet)) {
  //     return (
  //       <div>
  //         <button className="ui button" id="adopt-btn" onClick={()=>this.props.removeFromMyPets(matchedPet.id)}>Remove From My Pets</button>
  //       </div>
  //     )
    // } else if (pet.adopted && myPetMatchIds.includes(this.props.pet.id)) {
    //   return (
    //     <div>
    //       <button className="ui button" id="adopt-btn" onClick={()=>this.props.removeFromMyPets(matchedPet.id)}>Remove From My Pets</button>
    //     </div>
    //   )
  } else if (this.props.pet.owner_id === currentUser.id) {
      return (
        <div>
          <span img="𝔹𝕐 𝓤 ♥️"> By You ♡ </span>
        </div>
      )
    // } else if (myPetMatchIds.includes(pet.id) && this.props.myAdoptedPets.includes(pet)) {
    //   return (
    //     <div>
    //       <span img="𝔹𝕐 𝓤 ♥️"> By You ♡ </span>
    //     </div>
    //   )
  } else if (pet.adopted && !this.props.myAdoptedPets.includes(pet)) {
        return (
          <div>
            <span img="♥️"> ♡ </span>
          </div>
        )
      }
  }

  render() {
    return (
      <span>
        {/* {this.props.foundPet.adopted ? <p className="ui button">Adopted! </p> : <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(this.props.currentUser.id, this.props.foundPet.id)}>Adopt This Pet</button>} */}
        {this.handleAddOrRemove(this.props.currentUser, this.props.pet.id)}
      </span>
    )
    }
  }

export default MyPetsButton;
