import React from 'react';
import MyPets from './MyPets';
import {Link} from 'react-router-dom';

class ProfileContainer extends React.Component {

  handleAdoptedStatus = (userId, petId, foundPet) => {
    console.log(foundPet)
    // let matchedPets = this.props.myPets.map(myPet => myPet.pet)
    // console.log("matchedPets", matchedPets)
    // let adoptPet = matchedPets.find(pet => pet.id === petId)
    // console.log("adoptPet", adoptPet)
    if (this.props.petObj.adopted) {
      return <button className= "ui button">Adopted!</button>
    } else {
      return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, petId)}>Adopt</button>
    }
  }

        // render() {
        //   console.log(this.props.myPets)
      myPetCards = () => {
          return(
            <div className="ui container">
              <Link to='/' className="headerThree">Back to Pet Index</Link>
              <h2 className="headerFour">{this.props.currentUser.name}'s Pet List </h2>
              {/* <h1>My Pets List</h1> */}
              <br/><br/><br/>
              <div className="ui container">
                <div className="ui page grid">
                  {
                    this.props.myPets.map(mypet => {
                      return <MyPets key={mypet.pet_id} {...mypet} pets={this.props.pets}
                        handleAdopt={this.props.handleAdopt} currentUser={this.props.currentUser}
                        adopted={this.props.adopted}
                        petObj={this.props.petObj}
                        handleAdoptedStatus={this.handleAdoptedStatus}
                             /> })
                  }
                </div>
              </div>
            </div>
          )
        }
        // }
      render() {
        return (
          <div>
            {this.myPetCards()}
          </div>
            )
            }
            }
            export default ProfileContainer;
