import React from "react"
import Dashboard from './Dashboard';
import Signin from './components/Signin'
import Signup from './components/Signup'
import { Route } from 'react-router-dom';

class App extends React.Component {
  state = {
    pets:[],
    clicked:false,
    loggedIn: false
  }

  componentDidMount() {
    fetch('http://localhost:3000/api/v1/pets')
      .then(response => response.json())
      .then(data => {
        this.setState({
          pets: data
        })
      })
  }

  handleClick = () => {
    this.setState({
      loggedIn:!this.state.loggedIn
    })
  }

  // handleSorted = (event) => {
  //   let sortedNameCopyPets = [...this.state.pets].sort((a,b) => (a.name.localeCompare(b.name)))
  //   if (event.target.value === "name"){
  //     this.setState({
  //     pets:sortedNameCopyPets
  //     })}
  //   else if (event.target.value === "all"){
  //     this.setState({
  //     pets:this.state.pets
  //     })
  //   }
  // }

  render() {
    return <>
      <Route path='/' exact render={()=> <Dashboard pets={this.state.pets} handleSorted={this.handleSorted} loggedIn={this.state.loggedIn} />}/>
      <Route path='/signin' component={Signin }/>
      <Route path='/signup' render={(props) => <Signup {...props} />}
      />
    </>
  }
}

export default App
