  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state)
    fetch('http://localhost:3000/api/v1/sessions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(this.state)
    })
      .then(res => res.json())
