import React from 'react';

class MyPets extends React.Component {

  state = {
    clicked:false
  }

  handleClick = () => {
      this.setState({
        clicked:!this.state.clicked
      })
    }

    handleAdoptedStatus = (userId, petId, foundPet) => {
      console.log(foundPet)
      // let matchedPets = this.props.myPets.map(myPet => myPet.pet)
      // console.log("matchedPets", matchedPets)
      // let adoptPet = matchedPets.find(pet => pet.id === petId)
      // console.log("adoptPet", adoptPet)
      // if (this.props.adoptedPet.adopted && this.props.petObj.id === foundPet.id) {
      if (this.props.petObj.adopted) {
        return <button className= "ui button">Adopted!</button>
      } else {
        return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, petId)}>Adopt</button>
      }
    }

  render() {
    console.log(this.props)
    console.log("petObj in myPets", this.props.petObj)
    console.log("petObj.adopted in myPets", this.props.petObj.adopted)
    let foundPet = this.props.pets.find(pet => this.props.pet_id === pet.id)

      if(!this.state.clicked){
        return(
          <div className="petContainer">
            <div className="ui card content">
              <div className="myPetTile">
                <h1 className="myPetTileh3">{foundPet.name}</h1>
                <img className="ui inline image" src={(foundPet.photo)} />
                <button className= "ui button" onClick={this.handleClick}> View More Details! </button>
                {this.handleAdoptedStatus(this.props.currentUser.id, foundPet.id, foundPet)}
                {/* <button onClick={() => this.props.handleAdopt(this.props.currentUser.id, foundPet.id)} */}
                {/* className= "ui button">{this.props.adopted && foundPet.id === this.props.petObj.pet_id ? "Adopted!" : "Adopt"}</button> */}
              </div>
            </div>
            </div>
            )
          } else {
            return(
              <div className="petContainer">
                <div className="ui card image">
                  <div className="myPetTile">
                    <h1 className="myPetTileh3">{foundPet.name}</h1>
                    <h3> Description:{foundPet.description}</h3>
                    <h3> Sex:{foundPet.sex}</h3>
                    <h3> Animal:{foundPet.animal}</h3>
                    <h3> Age:{foundPet.age}</h3>
                    <h3> Breed: {foundPet.breed}</h3>
                    <button className= "ui button" onClick={this.handleClick}> Back </button>
                    {this.props.handleAdoptedStatus(this.props.currentUser.id, foundPet.id)}
                    {/* <button onClick={() => this.props.handleAdopt(this.props.currentUser.id, foundPet.id)} */}
                    {/* className= "ui button">{this.props.petObj.adopted && foundPet.id === this.props.petObj.pet_id ? "Adopted!" : "Adopt"}</button> */}
                    {/* className= "ui button">{this.props.petObj.adopted && foundPet.id === this.props.petObj.pet_id ? "Adopted!" : "Adopt"}</button> */}
                      </div>
                  </div>
                </div>
        )
      }
    }
}

export default MyPets;
