import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom';
import { Animated } from 'react-animated-css';
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";
import './App.css';
import './App.scss';
import doggo from './images/doggo.jpg';
import kitter from './images/kitter.jpg';
import catdog from './images/catdog.jpg';

const apiPetsAddress = 'http://localhost:3000/api/v1/pets'

class Welcome extends Component {
  state = {
    pets: []
  }

  handleClick = () => {
    console.log("figure out delay and animation")
  }

  componentDidMount() {
    fetch(apiPetsAddress)
    .then(response => response.json())
    .then(data => {
      console.log(date)
      this.setState({
        pets: data
      })
    })
  }

  gallery = () => {
    const handleOnDragStart = event => event.preventDefault()
    return (
      <AliceCarousel mouseDragEnabled >
        <img onDragStart={handleOnDragStart} src={doggo} alt="doggo" className="App-logo" style={{"maxHeight": "75vh"}}/>
        <img onDragStart={handleOnDragStart} src={kitter} alt="kitter" className="App-logo" style={{"maxHeight": "100vh", "horizontalAlign": "center", "verticalAlign": "middle"}}/>
        <img onDragStart={handleOnDragStart} src={catdog} alt="cat-and-dog" className="App-logo" style={{"maxHeight": "100vh"}}/>
      </AliceCarousel>
    )
  }

  render() {
    console.log(this.state)
    return (
      <div className="App">
        <header className="image">
          {this.gallery()}
          {/* <div class="carousel">
            <a class="carousel-item" href="#one!"><img src={doggo} alt="doggo" className="App-logo"/></a>
            <a class="carousel-item" href="#two!"><img src={kitter} alt="kitter" className="App-logo"/></a>
            <a class="carousel-item" href="#three!"><img src={catdog} alt="cat-and-dog" className="App-logo"/></a>
          </div> */}
          {/* <img src={doggo}
            alt="doggo" className="App-logo"
          /> */}
          {/* <img src={kitter}
            alt="kitter" className="App-logo"
          /> */}
          {/* <img src={catdog}
            alt="cat-and-dog" className="App-logo"
          /> */}
          <br/><br/><br/><br/>
          <div className="App-header">
            <Animated animationIn="pulse" animationOut="flash" isVisible={true}>
              <p className="App-title">Adopt A Pet!</p>
            </Animated>
            <br/><br/>
            <Link to='/signin' onClick={this.handleClick} className='App-link'>
              SignIn
            </Link>
            <br></br><br></br>
            <Link to='/signup' onClick={this.handleClick} className='App-link'>
              SignUp
            </Link>
          </div>
        </header>
      </div>
    );
  }
}

export default Welcome;
