import React from 'react';
import ProfileContainer from './ProfileContainer';

class Signin extends React.Component {
  state = {
    name: '',
    email: ''
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
    console.log(event.target.value)
    console.log("this.state", this.state)
  }

  handleSubmit = (event) => {
    event.preventDefault()
    fetch('http://localhost:3000/api/v1/users/')
    .then(r => r.json())
    .then(userData => {
      console.log(userData)
      const userObj = userData.find(user => user.name.toLowerCase === this.state.name.toLowerCase() && user.email.toLowerCase() === this.state.email.toLowerCase())
      console.log(userObj)
    })
    this.setState({
      name: '',
      email: ''
    })
  }
  render() {
    const signInForm =
      <div className="signinDiv">
        <h1 className="signinHeader">Sign In To Save Pets!</h1>
        <br/><br/>
        <div className="login">
          <form onSubmit={this.handleSubmit}>
            <input className="signinPlaceholders"
              name="name"
              placeholder="name"
            />
            <input className="signinPlaceholders"
              name="email"
              placeholder="email"
            />
            <input
              type="submit"
              className="signinButton"
            />
          </form>
        </div>
      </div>
    return signInForm;
  }
}

export default Signin;
