import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Animated } from 'react-animated-css';
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
import './App.css';
import './App.scss';
// import doggo from './images/doggo.jpg';
// import kitter from './images/kitter.jpg';
// import catdog from './images/catdog.jpg';

const api50PetsAddress = 'http://localhost:3000/api/v1/sample50pets'

class Welcome extends Component {
    state = {
      fiftyPets: [],
      firstSample: '',
      secondSample: '',
      thirdSample: '',
      fourthSample: '',
      fifthSample: ''
    }

  componentDidMount = () => {
    fetch(api50PetsAddress, {
      method: "GET",
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json",
      }
    })
    .then(response => response.json())
    .then(fiftyPets => {
      // let samplePet = ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] )
      this.setState({
        firstSample: ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] ),
        secondSample: ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] ),
        thirdSample: ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] ),
        fourthSample: ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] ),
        fifthSample: ( fiftyPets[Math.floor ( Math.random() * fiftyPets.length )] )
      })
    })
  }

  // getSamplePet = () => {
  //   // console.log(this.state.fiftyPets)
  //   let samplePet = ( this.state.fiftyPets[Math.floor ( Math.random() * this.state.fiftyPets.length )] )
  //   // let samplePetImage = samplePet.photo
  //   console.log("samplePet", samplePet)
  //   // console.log("samplePet.photo", samplePet['photo'])
  // }

  gallery = () => {
    // const handleOnDragStart = event => event.preventDefault()
    // <img onDragStart={handleOnDragStart} source={(this.state.firstSample)}  />

    return (
        <AliceCarousel >
          <div>
            <img source={(this.state.firstSample)}  />
            <img src={(this.state.secondSample)}  />
            <img src={(this.state.thirdSample)}  />
            <img src={(this.state.fourthSample)}  />
            <img src={(this.state.fifthSample)} />
          </div>
        </AliceCarousel>
    )
  }

  render() {
    console.log("this.state.firstSample", this.state.firstSample)
    console.log("this.state.firstSample", this.state.secondSample)
    console.log("this.state.firstSample", this.state.thirdSample)
    console.log("this.state.firstSample", this.state.fourthSample)
    console.log("this.state.firstSample", this.state.fifthSample)
    return (
      <div className="App">
        <header className="image">
          {this.gallery()}
          {/* <div class="carousel">
            <a class="carousel-item" href="#one!"><img src={doggo} alt="doggo" className="App-logo"/></a>
            <a class="carousel-item" href="#two!"><img src={kitter} alt="kitter" className="App-logo"/></a>
            <a class="carousel-item" href="#three!"><img src={catdog} alt="cat-and-dog" className="App-logo"/></a>
          </div> */}
          {/* <img src={doggo}
            alt="doggo" className="App-logo"
          /> */}
          {/* <img src={kitter}
            alt="kitter" className="App-logo"
          /> */}
          {/* <img src={catdog}
            alt="cat-and-dog" className="App-logo"
          /> */}
          <br/><br/><br/><br/>
          <div className="App-header">
            <Animated animationIn="pulse" animationOut="flash" isVisible={true}>
              <p className="App-title">Adopt A Pet!</p>
            </Animated>
            <br/><br/>
            <Link to='/signin' onClick={this.handleClick} className='App-link'>
              SignIn
            </Link>
            <br></br><br></br>
            <Link to='/signup' onClick={this.handleClick} className='App-link'>
              SignUp
            </Link>
          </div>
        </header>
      </div>
    );
  }
}

export default Welcome;
