import React from 'react';
import MyPetsButton from './MyPetsButton'

class MyPets extends React.Component {

  state = {
    clicked:false
  }

  handleClick = () => {
      this.setState({
        clicked:!this.state.clicked
      })
    }

    handleAdoptedStatus = (userId, foundPet) => {
      console.log(foundPet)
      console.log(this.props.adopted)
      // if (this.props.petObj.adopted && this.props.petObj.id === foundPet.id) {
      if (this.props.foundPet.adopted) {
        return <button className= "ui button">Adopted!</button>
      } else {
        return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, foundPet.id)}>Adopt</button>
      }
    }

  render() {
    let foundPet = this.props.pets.find(pet => this.props.pet_id === pet.id)
    if(!this.state.clicked){
      return(
        <div className="petContainer">
          <div className="ui card content">
            <div className="myPetTile">
              <h1 className="myPetTileh3">{foundPet.name}</h1>
              <img className="ui inline image" src={(foundPet.photo)} />
              <button className= "ui button" onClick={this.handleClick}> View More Details! </button>
              {this.handleAdoptedStatus(this.props.currentUser.id, foundPet)}
              {/* <MyPetsButton foundPet={foundPet} petObj={this.props.PetObj} currentUser={this.props.currentUser} handleAdopt={this.props.handleAdopt} /> */}
            </div>
          </div>
        </div>
      )
      } else {
      return(
        <div className="petContainer">
          <div className="ui card image">
            <div className="myPetTile">
              <h1 className="myPetTileh3">{foundPet.name}</h1>
              <h3> Description:{foundPet.description}</h3>
              <h3> Sex:{foundPet.sex}</h3>
              <h3> Animal:{foundPet.animal}</h3>
              <h3> Age:{foundPet.age}</h3>
              <h3> Breed: {foundPet.breed}</h3>
              <button className= "ui button" onClick={this.handleClick}> Back </button>
              {this.handleAdoptedStatus(this.props.currentUser.id, foundPet.id)}
              {/* <MyPetsButton foundPet={foundPet} petObj={this.props.PetObj} currentUser={this.props.currentUser} handleAdopt={this.props.handleAdopt} /> */}
            </div>
          </div>
        </div>
      )
    }
  }
}

export default MyPets;
