import React from 'react';
import PetCard from './PetCard'
import '../App.css'

class MyAdoptedPets extends React.Component {

  state = {
    shown: false
  }

  showAdopted = () => {
    this.setState({
      shown: !this.state.clicked
    })
  }

  render() {
    // console.log(this.props.foundPet)
    console.log(this.props.myAdoptedPets)
    if(this.state.clicked) {
    return (
      <div className="adoptedPets">
        <button onClick={this.showAdopted}>Hide Adopted</button>
        My Adopted Pets
        {
          this.props.myAdoptedPets.map(adoptedPet => {
            return(
              <ul key={adoptedPet.id}>
                {adoptedPet.name}
              </ul>
            )
          })
        }
      </div>
    )
  } else {
    return(
    <div className="adoptedPets">
      <button onClick={this.hideAdopted}>Show Adopted</button>
    </div>
  )}
    }
  }

export default MyAdoptedPets;
