import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom';
import { Animated } from 'react-animated-css';
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";
import './App.css';
import './App.scss';
import doggo from './images/doggo.jpg';
import kitter from './images/kitter.jpg';
import catdog from './images/catdog.jpg';

const   gallery = () => {
    const handleOnDragStart = event => event.preventDefault()
    return (
      <AliceCarousel mouseDragEnabled >
        <img onDragStart={handleOnDragStart} src={doggo} alt="doggo" className="App-logo" style={{"maxHeight": "75vh"}}/>
        <img onDragStart={handleOnDragStart} src={kitter} alt="kitter" className="App-logo" style={{"maxHeight": "100vh", "horizontalAlign": "center", "verticalAlign": "middle"}}/>
        <img onDragStart={handleOnDragStart} src={catdog} alt="cat-and-dog" className="App-logo" style={{"maxHeight": "100vh"}}/>
      </AliceCarousel>
    )
  }

class Welcome extends Component {

  // gallery = () => {
