  //     this.setState({
  //       myBots: myBotsCopy
  //     })
  //   }
  // }
  render() {
    console.log(this.props)
    let foundPet = this.props.pets.find(pet => this.props.pet_id === pet.id)
    console.log(foundPet)
      if(this.state.clicked){
        return(
          <div className="ui card image">
            <h1>{foundPet.pet.name}</h1>
            <img className="ui inline image" src={(foundPet.pet.photo)} />
            <button className= "ui button" onClick={this.handleClick}> View More Details! </button>
            <button onClick={()=>foundPet.handleMyPets(foundPet.currentUser.id, this.props.pet.id)} className= "ui button" >Add To My Pets</button>
          </div>
        )
      } else {
        return(
          <div className="ui card content">
            <h1>{foundPet.name}</h1>
            <h3> Description:{foundPet.description}</h3>
            <h3> Sex:{foundPet.sex}</h3>
            <h3> Animal:{foundPet.animal}</h3>
            <h3> Age:{foundPet.age}</h3>
            <h3> Breed: {foundPet.breed}</h3>
            {/* <button className= "ui button" onClick={this.handleClick}> Back </button> */}
            {/* <button onClick={()=>this.props.handleMyPets(this.props.pet.id, this.props.currentUser.id)} className= "ui button" >Add To My Pets</button> */}
          </div>
        )
      }
    }
}

export default MyPets
