import React from 'react';

class MyPets extends React.Component {

  state = {
    clicked:false
  }

  handleClick = () => {
      this.setState({
        clicked:!this.state.clicked
      })
    }

    handleAdoptedStatus = (userId, petId) => {
      console.log("petObj in handleAdoptedStatus", this.props.petObj)
      console.log("petId in handleAdoptedStatus", petId)
        // this.props.myPets.map(myPet => {
        //   console.log("myPet.pet_id in handleAdoptStatus", myPet.pet_id)
        // })
      let myMatchedPets = this.props.myPets.map(myPet => myPet.pet)
      let pet = myMatchedPets.find(myMatchedPet => myMatchedPet.id === petId)
      console.log("myMatchedPets", myMatchedPets)
      console.log("pet", pet)
      if (this.state.clickedToAdopt){
        return <button className="ui button">Adopted!</button>
      } else {
        return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, pet.id)}>Adopt This Pet</button>
      }
    }

  render() {
    console.log("this.props in myPets", this.props)
