import React from 'react';
import Dashboard from './Dashboard';
import Signin from './components/Signin';
import Signup from './components/Signup';
import { Route, withRouter } from 'react-router-dom';
import { BadTokenError } from './error';
import ProfileContainer from "./components/ProfileContainer";

const apiMatchesAddress = 'http://localhost:3000/api/v1/matches'
const apiUsersAddress = 'http://localhost:3000/api/v1/users'
const apiPetsAddress = 'http://localhost:3000/api/v1/pets'

class App extends React.Component {
  state = {
    username: null,
    userId: null,
    pets: [],
    clicked: false,
    currentUser: null,
    myPets:[],
    checkboxClick: false,
    animalCheck: "",
    adopted: false,
    petObj:"",
    myAdoptedPets: []
  }

  componentDidMount() {
    fetch(apiPetsAddress)
    .then(response => response.json())
    .then(data => {
      this.setState({
        pets: data
      })
