import React, { Component } from 'react';

// const adoptButton = document.querySelector("#adopt-button")

class PetCard extends Component {

  state = {
    clicked:false
  }

  handleClick = () => {
      this.setState({
        clicked:!this.state.clicked
      })
    }

  handleAdoptedStatus = () => {
    if (this.props.pet.adopted === true) {
      return <button className= "ui button">Adopted!</button>
    } else {
      return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleMyPets(this.props.pet.id, this.props.currentUser.id)}>Add To My Pets</button>
    }
  }

      show = () => {
        if(this.state.clicked){
          return(
            <div className="petContainer">
              <div className="ui card content">
                <div className="petTile">
                  <h1 className="petTileh3">{this.props.pet.name}</h1>
                  <h3> Description:{this.props.pet.description}</h3>
                  <h3> Sex:{this.props.pet.sex}</h3>
                  <h3> Animal:{this.props.pet.animal}</h3>
                  <h3> Age:{this.props.pet.age}</h3>
                  <h3> Breed: {this.props.pet.breed}</h3>
                  <button className= "ui button" onClick={this.handleClick}> Back </button>
                  {this.handleAdoptedStatus}
                  {/* <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleMyPets(this.props.pet.id, this.props.currentUser.id)}>Add To My Pets</button> */}
                </div>
          </div>
        </div>
      )
    } else {
      return(
        <div className="petContainer">
          <div className="ui card image">
            <div className="petTile">
              <h1 className="petTileh3">{this.props.pet.name}</h1>
              <img className="ui inline image" src={(this.props.pet.photo)} />
              <button className="ui button" onClick={this.handleClick}> View More Details! </button>
              {this.handleAdoptedStatus}
              {/* <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleMyPets(this.props.currentUser.id, this.props.pet.id)}>Add To My Pets</button> */}
              {/* <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleMyPets(this.props.pet.id, this.props.currentUser.id)}>{(this.props.pet.adopted ? "Adopted!" : "Add To My Pets")}</button> */}
            </div>
          </div>
      </div>
      )
    }
  }

  render() {
    // let adoptButton = document.getElementById("adopt-btn")
    console.log(this.props.adoptButton)
    // let adoptButton = document.querySelector("#adopt-button")
    return (
        <div >
          {this.show()}
        </div>
      );
  }
}

export default PetCard;
