import React from 'react';
import MyPets from './MyPets';
import PetCard from './PetCard';
import {Link} from 'react-router-dom';

class ProfileContainer extends React.Component {

  state = {
    adopted: ''
  }
  // this.setState({
  //   adopted:!this.state.adopted
  // })

  handleAdoptedStatus = (userId, petId) => {
    console.log("pet in handleAdoptedStatus", pet)
    console.log("petObj in handleAdoptedStatus", pet)
    console.log("petId in handleAdoptedStatus", petId)
    this.props.myPets.map(myPet => {
      console.log("myPet.pet_id in handleAdoptStatus", myPet.pet_id)
    })
    console.log("petId in handleAdoptedStatus", petId)
    let myMatchedPets = this.props.myPets.map(myPet => myPet.pet)
    let pet = myMatchedPets.find(myMatchedPet => myMatchedPet.id === petId)
console.log("myMatchedPets", myMatchedPets)
console.log("pet", pet)
    // if (pet.adopted && pet.id === this.props.petObj.pet_id) {
    if (this.props.adopted) {
      return <button className="ui button">Adopted!</button>
    } else {
      return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, pet.id)}>Adopt This Pet</button>
    }
    // return <button className="ui button" onClick={this.props.handleAdopt(userId, pet.id)}>{pet.adopted && pet.id === this.props.petObj.pet_id ? "Adopted!" : "Adopt"}</button>
}

  render() {
    this.props.myPets.map(mypet => {
      console.log("mypet.pet in profile container render", mypet.pet)
    })
    return(
      <div className="ui container">
        <Link to='/' className="headerThree">Back to Pet Index</Link>
        <h2 className="headerFour">{this.props.currentUser.name}'s Pet List </h2>
        {/* <h1>My Pets List</h1> */}
        <br/><br/><br/>
        <div className="ui container">
          <div className="ui page grid">
            {
              this.props.myPets.map(mypet => {
                return <PetCard key={mypet.pet_id} pet={mypet.pet} pets={this.props.pets}
                  handleAdopt={this.props.handleAdopt}
                  currentUser={this.props.currentUser}
                  adopted={this.props.adopted}
                  // petObj={this.props.petObj}
                  // pet={this.props.petObj}
                  // pet={mypet}
                  handleAdoptedStatus={this.handleAdoptedStatus}
                  // handleAdoptedStatus={this.handleAdoptedStatus}
                  /> })
            }
          </div>
        </div>
      </div>
    )
  }
}

export default ProfileContainer;
