import React from 'react';
import PetCard from './PetCard'

class MyPetsButton extends React.Component {
  // 
  // handleThesePets = () => {
  //   if (this.props.pet.adopted){
  //     return <p className="ui button">Adopted!</p>
  //   } else {
  //     return <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(userId, pet.id)}>Adopt This Pet</button>
  //   }
  // }

  render() {
    let myMatchedPets = this.props.myPets.map(myPet => myPet.pet)
    let pet = myMatchedPets.find(myMatchedPet => myMatchedPet.id === this.props.pet.id)
    console.log("myMatchedPets", myMatchedPets)
    console.log("pet", pet)
    return (
      <span>
        {pet.adopted ? <p className="ui button">Adopted! </p> : <button className="ui button" id="adopt-btn" onClick={()=>this.props.handleAdopt(this.props.user.id, pet.id)}>Adopt This Pet</button>}
      </span>
    )
    }
  }

export default MyPetsButton;
